/* --------------------------------------------------------------------------
 * Typography
 * -------------------------------------------------------------------------- */
/* Global Defaults */
html, body,
input, button,
select, textarea {
  font: 1em/1.6 "Domaine-Text-Sans", "Helvetica Neue", sans-serif;
  text-rendering: optimizeLegibility;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  font-smoothing: antialiased;
  font-synthesis: none; }

p {
  margin: 0 0 1rem 0; }

b, strong {
  font-family: "Domaine-Text-Sans", "Helvetica Neue", sans-serif;
  font-weight: bold; }

i, em {
  font-family: "Domaine-Text-Sans", "Helvetica Neue", sans-serif;
  font-style: italic; }

ul, ol {
  margin: 0;
  padding: 0;
  list-style-type: none; }
  ul.-reset, ol.-reset {
    list-style-type: none;
    padding: 0;
    margin: 0; }
    ul.-reset ul, ul.-reset ol, ol.-reset ul, ol.-reset ol {
      list-style-type: none;
      padding: 0;
      margin: 0; }

a {
  outline: none;
  text-decoration: none;
  color: inherit; }

::-moz-selection {
  background: none;
  color: #A69A8D; }

::selection {
  background: none;
  color: #A69A8D; }

::-moz-selection {
  background: none;
  color: #A69A8D; }

/* Typography Helper Classes
 * -------------------------------------------------------------------------- */
/**
 * Text sizes
 */
.txt-xs {
  font-size: 0.6rem; }
  @media only screen and (min-width: 90em) {
    .txt-xs {
      font-size: 0.8rem; } }

.txt-s {
  font-size: 1rem; }

.txt {
  font-size: 1.125rem; }
  @media only screen and (min-width: 40em) {
    .txt {
      font-size: 2.7vw; } }
  @media only screen and (min-width: 60em) {
    .txt {
      font-size: 1.5rem; } }

.txt-l {
  font-size: 1.5rem;
  line-height: 1.2; }
  @media only screen and (min-width: 40em) {
    .txt-l {
      font-size: 3vw; } }
  @media only screen and (min-width: 60em) {
    .txt-l {
      font-size: 2rem; } }

.txt-xl {
  font-size: 2rem;
  line-height: 1.1; }
  @media only screen and (min-width: 40em) {
    .txt-xl {
      font-size: 4vw; } }
  @media only screen and (min-width: 90em) {
    .txt-xl {
      font-size: 3.5rem; } }

.txt-xxl {
  font-size: 2.5rem;
  line-height: 1; }
  @media only screen and (min-width: 40em) {
    .txt-xxl {
      font-size: 6vw; } }
  @media only screen and (min-width: 90em) {
    .txt-xxl {
      font-size: 4.5rem; } }

/**
 * Text alignment
 */
.txt-center {
